
import { Idioma, Palabra, Registro } from './data';


export function mostrarIdiomas(idiomas: Idioma[]): void {
    console.log("Recorriendo y mostrando elementos de idiomas usando forEach:");
    idiomas.forEach(idioma => {
        console.log(idioma);
    });
}


