
import { idiomas, palabras, registros } from './data';
import { mostrarIdiomas, mostrarPalabras, mostrarRegistros } from './funciones';
import { buscarObjetoPorId } from './utilidades';


mostrarIdiomas(idiomas);
mostrarPalabras(palabras);
mostrarRegistros(registros);

// Buscar objeto por ID y mostrarlo
const idBuscado = 3; // ID a buscar
console.log(`\nBuscando objeto con ID ${idBuscado}:`);
buscarObjetoPorId(registros, idBuscado, (error, objeto) => {
    if (error) {
        console.log(error);
    } else {
        console.log(objeto);
    }
});
