
import { Registro } from './data';

// funciÃ³n con Callback para buscar un objeto por su ID
export function buscarObjetoPorId(arr: Registro[], id: number, callback: (error: string | null, objeto: Registro | null) => void): void {
    let objetoEncontrado: Registro | null = null;
    arr.forEach(registro => {
        if (registro.id === id) {
            objetoEncontrado = registro;
        }
    });

    if (objetoEncontrado) {
        callback(null, objetoEncontrado);
    } else {
        callback("Objeto no encontrado", null);
    }
}
